import React, { useState } from 'react';

import { createStage } from '../gameHelpers';


//Import Styled Componenst
import { StyledTetrisWrapper, StyledTetris } from './styles/StyledTetris';

//Custom Hooks
import { usePlayer } from '../hooks/usePlayer';
import { useStage } from '../hooks/useStage';

// components
import Stage from './Stage';

import Display from './Display';

import StartButton from './StartButton';


const Tetris = () => {

  //Speed of the drop in the game depending the Level
  const [dropTime, setDropTime] = useState(null);

  //When the game is Over
  const [gameOver, setGameOver] = useState(false);


  const [player, updatePlayerPos, resetPlayer] = usePlayer();

  //Send the player to the stage
  const [stage, setStage] = useStage(player);

  // console.log(createStage());

  console.log('re-render');

  //Player movement, left and right
  const movePlayer = dir => {
    updatePlayerPos({ x: dir, y: 0 });
  }

  //Starting the Game
  const startGame = () => {

    //Reset everything
    setStage(createStage());
    resetPlayer();

  }


  const drop = () => {

    //Update player position
    updatePlayerPos({ x: 0, y: 1, collided: false})
  }

  //Function to go down the player
  const dropPlayer = () => {
    drop();
  }


  //Callback function when we press the keys in the keyboard
  const move = ({ keyCode }) => {

    //Check that the game is not Over
    if(!gameOver){
      if(keyCode === 37){ //left key arrow on the keyboard
        movePlayer(-1); //moving to the left
      } else if (keyCode === 39 ){ //right key arrow on the keyboard
        movePlayer(1); //moving to the right
      } else if (keyCode === 40){ //down key arrow on the keyboard
        dropPlayer(); //go down
      }
    }
  }



  return(
    //Wrap with our background Image, Callback function when a key is press on the screen
    <StyledTetrisWrapper role="button" tabIndex="0" onKeyDown={e => move(e)}>

      <StyledTetris>

      <Stage stage={stage} />


      <aside>

        //If the game is over Display gameOver
        {gameOver ? (
          <Display gameOver={gameOver} text="Game Over" />
        ) : (

       //call the startGame function on click start button
        <div>
         <Display text="Score" />
         <Display text="Rows" />
         <Display text="Level" />
        </div>

       )}

        <StartButton onClick={startGame}/>

      </aside>

      </StyledTetris>

    </StyledTetrisWrapper>

  )
}

export default Tetris;
