import React from 'react';

import './Register.css';

class Register extends React.Component{

  constructor(props){

    super(props);

    this.state = {
      email: '',
      password: '',
      name: ''
    }
  }

  onNameChange = (event) => {
    this.setState({name: event.target.value})
  }

  onEmailChange = (event) => {
    this.setState({email: event.target.value})
  }

  onPasswordChange = (event) => {
    this.setState({password: event.target.value})
  }


  //Connect to Server

  render(){

  return (

  <article className="sign br3 ba b--black-10 mv4 w-100 w-50-m w-25-l shadow-5 mw6 center">

    <main className="pa4 black-80">
       <div className="measure">
        <fieldset id="register" className="ba b--transparent ph0 mh0">
          <legend className="f1 fw6 ph0 mh0">Register</legend>
          <div className="mt3">
           <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
           <input className="inp pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="text" name="name"  id="name" />
          </div>
          <div className="mt3">
           <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
           <input className="inp pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address" />
          </div>
          <div className="mv3">
           <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
           <input className="inp b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" />
         </div>

         <div className="btn">
          <input onClick={() => onRouteChange('home')} className="in b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" type="submit" value="Register" />
         </div>

       </fieldset>

      </div>
  </main>
</article>

  );
 }

}

export default Register;
